const menuBtn=document.getElementById("menu-btn"),mainMenu=document.getElementById("main-menu"),menuLinkList=document.getElementById("links-menu"),toggleMenuVisible=()=>{mainMenu.classList.contains("dropdown-visible")?mainMenu.classList.remove("dropdown-visible"):mainMenu.classList.add("dropdown-visible")},onClickedLink=e=>{e.target&&"SPAN"==e.target.nodeName&&toggleMenuVisible()};menuBtn.addEventListener("click",toggleMenuVisible),menuLinkList.addEventListener("click",onClickedLink);